let petsDataOur = [];

for(let i = 0; i < 6; i++){
    for (let j = i*8; j < i*8 + 8; j++){
      petsDataOur.push({})
      Object.assign(petsDataOur[j],petsData[j%8]);
      petsDataOur[j].name += ' №'+ (i+1);
    }
}



let pets = {
  name: document.getElementsByClassName('pet_name_our'),
  img: document.getElementsByClassName('pet_img_our')
}

let bufferPets = [];

for (let i = 0; i < pets.name.length; i++){
  pets.name[i].innerHTML = petsDataOur[i].name;
  pets.img[i].src = petsDataOur[i].img;
  bufferPets.push(petsDataOur[i])
}

var pageCout= 0;
var cardAmount = 3;
if (document.body.clientWidth > 1280){
 cardAmount = 8;
} else if(document.body.clientWidth>768){
  cardAmount = 6;
} 
console.log(cardAmount)
const changePage = () =>{
  
  for (let i = 0; i < cardAmount; i++){

    pets.name[i].innerHTML = petsDataOur[pageCout*cardAmount + i].name;
    pets.img[i].src = petsDataOur[pageCout*cardAmount + i].img;
    bufferPets.push(petsDataOur[pageCout*cardAmount + i]);
  }

  bufferPets = bufferPets.slice(cardAmount,bufferPets.length);
  console.log(bufferPets)
  pageCircle.innerHTML = (pageCout+1);
  
  switch (pageCout){
    case 0:  
      arrLeftOur.setAttribute("disabled", "disabled");
      arrStart.setAttribute("disabled", "disabled");
      arrRightOur.removeAttribute("disabled", "disabled");
      arrEnd.removeAttribute("disabled", "disabled");
      break;
    case (Math.ceil(48/cardAmount -1)): 
      arrRightOur.setAttribute("disabled", "disabled");
      arrEnd.setAttribute("disabled", "disabled");
      arrLeftOur.removeAttribute("disabled", "disabled");
      arrStart.removeAttribute("disabled", "disabled");
      console.log(Math.ceil(48/cardAmount -1) == pageCout)
      break;
  }
  if (pageCout > 0){
      arrLeftOur.removeAttribute("disabled", "disabled");
      arrStart.removeAttribute("disabled", "disabled");
  }
  console.log(Math.ceil(48/cardAmount - 1),pageCout)
  if(Math.ceil(48/cardAmount - 1) > pageCout){
    arrRightOur.removeAttribute("disabled", "disabled");
    arrEnd.removeAttribute("disabled", "disabled");
  }
}

const turnRight = () => {
  pageCout++;
  changePage();

}
const turnLeft = () => {
  pageCout--;
  changePage();
}

const turnStart = () => {
  pageCout=0;
  changePage();
}

const turnEnd = () => {
  pageCout = Math.ceil(48/cardAmount-1);
  changePage();
}


const arrLeftOur = document.getElementById('arrow_left');
const arrRightOur = document.getElementById('arrow_right');
const arrStart = document.getElementById('arrow_start');
const arrEnd = document.getElementById('arrow_end');
const pageCircle = document.getElementById('page_count');

arrLeftOur.addEventListener('click',turnLeft);
arrLeftOur.setAttribute("disabled", "disabled");
arrStart.addEventListener('click',turnStart);
arrStart.setAttribute("disabled", "disabled");

arrRightOur.addEventListener('click',turnRight);
arrEnd.addEventListener('click',turnEnd);
/*slider end*/

/*popup*/
const popup = document.getElementById('popup');

const cards = document.getElementsByClassName('card_our');

const modalWindow = document.getElementById('modal_window');
const modalImg = document.getElementById('modal_img');
const modalName = document.getElementById('modal_name');
const modalSub = document.getElementById('modal_sub');
const modalInfo = document.getElementById('modal_info');
const modalAge = document.getElementById('modal_age');
const modalInoc = document.getElementById('modal_inoculations');
const modalDis = document.getElementById('modal_diseases');
const modalPar = document.getElementById('modal_parasites');

const cross = document.getElementById('cross');

popup.addEventListener('click', (e) => {
  if(e.target == popup || e.target == cross){
    body.classList.toggle('active-mobile-menu');
    popup.classList.toggle('nonvision');
    popup.classList.toggle('vision');
  }
});

for (let i = 0; i < cards.length; i++){
  cards[i].addEventListener('click', () => {
    body.classList.toggle('active-mobile-menu');
    popup.classList.toggle('nonvision');
    popup.classList.toggle('vision');
    console.log(bufferPets[i])
    modalImg.src = bufferPets[i].img;
    modalName.innerHTML = bufferPets[i].name
    modalSub.innerHTML = bufferPets[i].breed;
    modalInfo.innerHTML = bufferPets[i].description;
    modalAge.innerHTML = ' ' + bufferPets[i].age;
    modalInoc.innerHTML = ' ' + bufferPets[i].inoculations;
    modalDis.innerHTML = ' ' + bufferPets[i].diseases;
    modalPar.innerHTML = ' ' + bufferPets[i].parasites;
  });
}



modalWindow.addEventListener('mouseenter', () => {
  cross.classList.remove('cross_hover');
})
modalWindow.addEventListener('mouseleave', () =>{
  cross.classList.add('cross_hover');
})

/*popup end*/

